(4077, 96, NULL, 5, 'وایر فریم سایت شرکتی shamimadl.com', '<p><strong><span dir=\"RTL\">راه اندازی سایت شرکتی با امکانات زیر:</span></strong></p>\r\n\r\n<p><span dir=\"RTL\">نمایش نمونه کارها ، درج لوگو، نام و توضیحات وبسایت، جست و جو، اسلاید شو، منوی داینامیک با تعداد و زیر منوی دلخواه، آخرین اخبار و مقالات، بخش نظرات، گالری تصاویر، برگه ها، نوشته ها، آرشیو مطالب، دکمه های شبکه های اجتماعی شامل: فیس بوک، تلگرام، اینستاگرام وتوییتر، تماس با ما، درباره ما، نمایش کلمات کلیدی در انتهای مطالب، معرفی محصول، بنر تبلیغات تصویری خدمات شما در سایت ، ماژول امنیت و ماژول سئو، ماژول بهینه سازی سرعت سایت، آموزش مدیریت سایت، ریسپانسیو نسخه موبایل از سایز 480 پیکسل با بوت استراپ،</span><span dir=\"RTL\"> راه اندازی سرویس چت آنلاین با بازدیدکنندگان سایت </span></p>', '2022-07-12', '2022-07-17', 'done', NULL, '2022-07-12 04:44:11', '2022-07-12 12:01:12', '1d', '15:15:00', '16:30:00', '2022-07-12 12:01:12', NULL),
(4078, 86, NULL, 5, 'ویرایشات سپیدصنعت', '<p>سپیدصنعت<br />\r\nsepidsanat7@gmail.com<br />\r\nارسال شده امروز در 11:31</p>\r\n\r\n<p>باسلام<br />\r\nYour comment جدول از قسمت محصولات حذف شود<br />\r\nشماره های صفحه عربی و انگلیسی اصلاح بشه و شماره های شرکت گذاشته بشه<br />\r\nلوکیشن ها در قسمت عربی و انگلیسی کاشان رو نشون میده لطفا اصلاح بشه<br />\r\nصفحه فارسی و عربی مثل صفحه فارسی اصلاح نشده و منو نداره و آرم سپید صنعت مثل صفحه فارسی نیست</p>\r\n\r\n<p>صفحه عربی و انگلیسی باید مثل صفحه فارسی باشد<br />\r\nاصلا صفحه انگلیسی نمیاد و اپلود نمیشه<br />\r\nقسمت عربی و انگلیسی واتساپ رو لینک نکردین رو صفحه<br />\r\nوقتی در قسمت عربی میریم چرا تو پنل بالا صفحه اسم وب یار میاد لطفا به اسم شرکت سپید صنعت ذخیره بشه</p>\r\n\r\n<p>منو سپید صنعت به عربی ترجمه شده لطفا در قسمت منو جای گذاری کنید</p>\r\n\r\n<p>سینی کابل و نردبان و متعلقات: علبة الكابلات والسلم والاكسسوارات</p>\r\n\r\n<p>تبدیل به چپ سینی کابل: انعطف إلى اليسار في علبة الكابلات<br />\r\nتبدیل به راست سینی کابل: اقلب درج الكابلات إلى اليمين<br />\r\nتبدیل وسط سینی کابل: اتجه إلى منتصف درج الكابلات<br />\r\nسه راهی سینی کابل: صينية كابول تي شيرت<br />\r\nچهاراهی سینی کابل: مفترق طرق صينية كابول<br />\r\nزانو افقی ۴۵ درجه سینی کابل: صينية أفقية للركبة بزاوية 45 درجة<br />\r\nزانو ۹۰ درجه سینی کابل: صينية كابل 90 درجة للركبة<br />\r\nزانو بالارونده قابل تنظیم سینی کابل: درج كابل رفع الركبة قابل للتعديل<br />\r\nزانو پایین رونده قابل تنظیم سینی کابل: علبة كابل الركبة الهابطة قابلة للتعديل<br />\r\nانواع ریل و چنل: أنواع القضبان والقنوات<br />\r\nپایه چنل: قاعدة القناة<br />\r\nانواع سینی کابل: أنواع حوامل الكابلات<br />\r\nانواع نردبان کابل و تجهیزات جانبی: أنواع سلالم الكابلات وملحقاتها<br />\r\nدانلود کاتالوگ: تحميل الكتالوج</p>\r\n\r\n<p>تجهیزات جانبی برق خورشیدی: ملحقات الطاقة الشمسية<br />\r\nپنل خورشیدی: لوحة شمسية<br />\r\nایزی لاک: قفل سهل<br />\r\nباتری: بطارية<br />\r\nشارژ کنترولر: متحكم الملئ<br />\r\nاینورتر: العاكس<br />\r\nاستراکچرها: الهياكل<br />\r\nریل و چنل: القضبان والقنوات<br />\r\nفرم ها: نماذج<br />\r\nاتصالات: روابط<br />\r\nدانلود کاتالوگ: تحميل الكتالوج</p>\r\n\r\n<p>ماشین سازی: تصنيع الآلة<br />\r\nدستگاه فرمینگ: آلة تشكيل<br />\r\nدستگاه پرس: يضعط<br />\r\nدستگاه وینچ: آلة الونش</p>\r\n\r\n<p>سبيد نيجين صناعة آسيا<br />\r\nسپید صنعت نگین آسیا</p>\r\n\r\n<p>اصفهان خیابان امام خمینیخیابان مینو کوچه شماره ۲۷ ایران بلوک درب سوم سمت راست</p>\r\n\r\n<p>أصفهان ، شارع الإمام الخميني ، شارع مينو ، الزقاق رقم 27 ، إيران ، كتلة الباب الثالث جهة اليمين</p>\r\n\r\n<p>محصولات سپید صنعت<br />\r\nمنتجات الصناعة البيضات</p>\r\n\r\n<p>ما را در شبکه های اجتماعی همراهی کنید<br />\r\nانضم إلينا على وسائل التواصل الاجتماعي</p>\r\n\r\n<p>منتجاتنا<br />\r\nمحصولات ما</p>\r\n\r\n<p>الصفحة الرئيسية<br />\r\nصفحه اصلی</p>\r\n\r\n<p>استشارات وتنفيذ درج الكابلات وسلم الكابلات<br />\r\nمشاوره و اجرا سینی کابل و نردبان کابل</p>\r\n\r\n<p>منتجات<br />\r\nمحصولات</p>\r\n\r\n<p>استشارة وتشغيل الآلات<br />\r\nمشاوره و راه اندازی ماشین آلات</p>\r\n\r\n<p>تركيب معدات الطاقة الشمسية<br />\r\nنصب تجهیزات خورشیدی</p>\r\n\r\n<p>الشهادات<br />\r\nگواهینامه ها</p>\r\n\r\n<p>صالة عرض<br />\r\nگالری</p>\r\n\r\n<p>مقالات<br />\r\nمقالات</p>\r\n\r\n<p>مندوب<br />\r\nنمایندگان</p>\r\n\r\n<p>معلومات عنا<br />\r\nدرباره ما</p>\r\n\r\n<p>اتصل بنا<br />\r\nتماس با ما</p>', '2022-07-12', '2022-07-12', 'done', NULL, '2022-07-12 04:51:36', '2022-07-20 04:17:53', NULL, '14:30:00', '15:00:00', '2022-07-20 04:17:53', NULL),
